import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import { Typography } from '@mui/material';

import logo from '../Assets/navbarlogo.png';

const Navbar = () => {
  const [menu, setMenu] = useState("shop");
  const [categories, setCategories] = useState([]);
  const [userData, setUserData] = useState({
    profile: '' // Placeholder for the profile image URL
  });

  useEffect(() => {
    fetch('/getcategories.json') // Adjust the path if necessary
      .then(response => response.json())
      .then(data => {
        setCategories(data);
      })
      .catch(error => {
        console.error('Error fetching categories:', error);
      });

    fetch('/getuserinfo.json') // Adjust the path if necessary
      .then(response => response.json())
      .then(data => {
        setUserData(data);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
      });
  }, []);

  return (
    <div className='navbar'>
      <div className="nav-logo">
        <img src={logo} alt="" />
        <Link to="/home">
          <p>SLUGSHOP</p>
        </Link>
      </div>
      <ul className="nav-menu">
        <li onClick={() => setMenu("shop")}>
          <Link style={{ textDecoration: 'none' }} to='/'>
            Home
          </Link>
          {menu === "shop" ? <hr /> : <></>}
        </li>
        {categories.map((category, i) => (
          <li key={i} onClick={() => setMenu(category.category)}>
            <Link style={{ textDecoration: 'none' }} to={`/listings?category=${category.category}`}>
              {category.category.charAt(0).toUpperCase() + category.category.slice(1)}
            </Link>
            {menu === category.category ? <hr /> : <></>}
          </li>
        ))}
      </ul>
      <div className=" pt-5">
          <div className="bg-yellow-500 rounded-md w-[7rem]">
              <Link to="/create" className=" pl-2 bg-yellow-500 rounded w-28 btn btn-primary">
                  Create Post
              </Link>
          </div>
      </div>

      <div className="nav-profile">
        <Link to='/profile'>
          <img 
            src={userData.profile} 
            alt="Profile" 
            className="nav-profile-picture" // Use a specific class for styling
          />
        </Link>
      </div>
    </div>
  );
}

export default Navbar;
