const cleanRepeatedComments = require('./src/utils').cleanRepeatedComments

var fs = require('fs');
fs.readFile('lots.css', (err, buf) => {
  let css = buf.toString()
  const once = {}
  css = css.replace(/\/\*\![\s\S]*?\*\/\n*/gm, (match, ...args) => {
    if (once[match]) {
      return ''
    }
    once[match] = true
    return match
  })
  console.log(css);

});
